import React from 'react';
import { Button, Container, Row } from 'react-bootstrap';
import { Header } from '../components/Header.js';
import logo from '../images/logo.png';

export class Home extends React.Component {
  state = {
    is_dark: true,
    theme: 'theme_dark'
  }

  handleThemeChange = (new_theme) => {
      const current_theme = new_theme ? 'theme_dark' : 'theme_light';

      this.setState({
        is_dark: new_theme,
        theme: current_theme
      });
      this.props.onChangeTheme(current_theme);  
  }

    render() {
      return (
        <div className={"App Home "+this.state.theme}>
        <Header onChangeTheme={this.handleThemeChange}></Header>
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" />
          </header>
          <div>
            <Container className="App-content">
                <Row className="Link-padding">
                     <a className="App-link" href="#login">Login</a> | <a className="App-link" href="#sign_up">Sign Up</a>
                </Row>
                <Row>
                    <Button style={{borderWidth: 2, borderRadius: 20 }} className="App-button">GET STARTED</Button>
                </Row>
            </Container>
          </div>
        </div>
      );
    }
  }
