import React, { useState, useCallback } from 'react';
import './fonts/rsufont/RSU_Regular.ttf'
import './fonts/rsufont/RSU_light.ttf'
import './fonts/rsufont/RSU_BOLD.ttf'
import './modules/css/App.css';
import Routes from "./modules/router/Routes.js";
import { BrowserRouter as Router, useLocation } from 'react-router-dom';

function App() {
  let location = useLocation();
  const [page, setPage] = useState('home');
  const [theme, setTheme] = useState('theme_dark');

  const handleNavbarThemeChange = useCallback((current_theme) => {
    setTheme(current_theme);
  },[setTheme]);

  const handlePage = useCallback((current_page) => {
    setPage(current_page);
  },[setPage]);


  return (
    <div className={"App "+theme}>
      <Router>
        <Routes theme={theme} onChangeTheme={handleNavbarThemeChange} onPage={handlePage}></Routes>
      </Router>
    </div>
  );
}

export default App;
