import React, { useState, useCallback } from 'react';
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import '../css/MySetting.css';

const MySetting = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [dropdownOpen,setDropdownOpen] = useState(false);

    const toggle = () => setDropdownOpen(!dropdownOpen);
    const submenuToggle = () => setIsOpen(!isOpen);

    const onMouseEnter = useCallback(() => {
      setDropdownOpen(true);
    },[setDropdownOpen]);

    const onMouseLeave = useCallback(() => {
      setDropdownOpen(false);
    },[setDropdownOpen]);


    return (
      <Dropdown className={props.theme} onMouseOver={onMouseEnter} onMouseLeave={onMouseLeave} isOpen={dropdownOpen} toggle={toggle}>
        <DropdownToggle tag="button" type="button" className="my-button my-setting" caret>
          <i className="fa fa-cog"></i>
        </DropdownToggle>
        <DropdownMenu  right>
          <DropdownItem>
            <Dropdown isOpen={isOpen} toggle={submenuToggle}>
              <DropdownToggle tag="button" type="button" className="my-button my-setting">
                Dropdown
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem>TH</DropdownItem>
                <DropdownItem>EN</DropdownItem>
              </DropdownMenu>
            </Dropdown>
          </DropdownItem>
          <DropdownItem>Voice Over</DropdownItem>
          <DropdownItem>Logout</DropdownItem>
        </DropdownMenu>
      </Dropdown>
    );
  }

  export default MySetting;
